import { Table } from "@prismicio/types-internal/lib/customtypes"
import { either } from "fp-ts"
import { pipe } from "fp-ts/lib/function"
import * as t from "io-ts"

import { isFieldDef, WithKey } from "../../utils"

const codec = t.strict({
	table: t.string,
})

export const TableDef = new t.Type<WithKey<Table>>(
	"TableDef",
	(u: unknown): u is WithKey<Table> => isFieldDef(u) && Table.is(u.def),
	(u: unknown) => {
		return pipe(
			codec.decode(u),
			either.map(
				({ table }): WithKey<Table> => ({
					key: table,
					def: {
						type: "Table",
					},
				}),
			),
		)
	},
	(n) => n,
)
