import type { BooleanContent } from "@prismicio/types-internal/lib/content"
import type { BooleanField } from "@prismicio/types-internal/lib/customtypes"

import type { Renderer } from "../../models"

const BooleanRenderer: Renderer<BooleanField, BooleanContent> = {
	renderV1(content: BooleanContent): unknown {
		return {
			type: "Boolean",
			value: content.value,
		}
	},

	renderV2(_def: BooleanField, content: BooleanContent): unknown {
		return content.value
	},

	renderDefault(def: BooleanField): unknown {
		if (def.config?.default_value !== undefined) return def.config?.default_value
		return null
	},

	renderMocks(def: BooleanField, content: BooleanContent): unknown {
		return this.renderV2(def, content)
	},
}

export default BooleanRenderer
